    public function paginaGalleriaFunction()
    {
        return view('galleria', ['galleryFuorilegge' => $this->galleryFuorilegge]);
    }
    
    

    public function paginaDettagliFuorileggeFunction($uri)
    {
        foreach ($this->galleryFuorilegge as $fuorilegge) {
            if ($fuorilegge['uri'] == $uri) {
                return view('dettagliFuorilegge', [
                    'galleryFuorilegge' => $this->galleryFuorilegge,
                    'fuorilegge' => $fuorilegge
                ]);
            }
        }
    }
